<!DOCTYPE html>
<html>
<head>
    <title>Task Manager</title>
    <style>
        body {
            background: #f0f0f0;
            font-family: Arial, sans-serif;
            padding: 20px;
            color: #333;
        }
        #task-list {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
            gap: 20px;
        }
        .task {
            background: #e0e0e0;
            padding: 15px;
            border-radius: 5px;
        }
        .task-content {
            font-weight: bold;
            margin-bottom: 5px;
        }
        .task-status {
            margin: 5px 0;
        }
        input, button {
            padding: 8px;
            margin: 5px 0;
        }
        button {
            background: #ddd;
            border: none;
        }
        button:hover {
            background: #ccc;
        }
    </style>
</head>
<body>
    <h1>Task Manager</h1>
    <form id="task-form">
        <input type="text" id="task-content" placeholder="Task content" required>
        <input type="date" id="task-deadline">
        <button type="submit">Add Task</button>
    </form>
    <div id="task-list"></div>

    <script>
        let db;

        const dbName = 'taskDB';
        const storeName = 'tasks';

        function openDB() {
            return new Promise((resolve, reject) => {
                const request = indexedDB.open(dbName, 1);
                request.onupgradeneeded = () => {
                    const db = request.result;
                    if (!db.objectStoreNames.contains(storeName)) {
                        db.createObjectStore(storeName, { keyPath: 'id' });
                    }
                };
                request.onsuccess = () => resolve(request.result);
                request.onerror = () => reject(request.error);
            });
        }

        async function addTask(task) {
            const db = await openDB();
            const transaction = db.transaction(storeName, 'readwrite');
            const store = transaction.objectStore(storeName);
            store.add(task);
            await transaction.complete;
            db.close();
        }

        async function getTasks() {
            const db = await openDB();
            const transaction = db.transaction(storeName, 'readonly');
            const store = transaction.objectStore(storeName);
            const request = store.getAll();
            await transaction.complete;
            db.close();
            return request;
        }

        async function deleteTask(id) {
            const db = await openDB();
            const transaction = db.transaction(storeName, 'readwrite');
            const store = transaction.objectStore(storeName);
            store.delete(id);
            await transaction.complete;
            db.close();
        }

        async function updateTask(id, updates) {
            const db = await openDB();
            const transaction = db.transaction(storeName, 'readwrite');
            const store = transaction.objectStore(storeName);
            store.put({ id, ...updates });
            await transaction.complete;
            db.close();
        }

        document.getElementById('task-form').addEventListener('submit', async (e) => {
            e.preventDefault();
            const content = document.getElementById('task-content').value;
            const deadlineInput = document.getElementById('task-deadline');
            const deadline = new Date(deadlineInput.value);
            if (isNaN(deadline)) {
                alert('Invalid deadline date');
                return;
            }
            const task = {
                id: Date.now(),
                content,
                status: 'uncompleted',
                registrationDate: Date.now(),
                deadline: deadline.getTime(),
                completionDate: null
            };
            await addTask(task);
            document.getElementById('task-content').value = '';
            document.getElementById('task-deadline').value = '';
            renderTasks();
        });

        document.getElementById('task-list').addEventListener('click', async (e) => {
            if (e.target.tagName === 'BUTTON') {
                const taskId = parseInt(e.target.dataset.taskId);
                if (e.target.dataset.action === 'delete') {
                    await deleteTask(taskId);
                } else if (e.target.dataset.action === 'update') {
                    const db = await openDB();
                    const transaction = db.transaction(storeName, 'readwrite');
                    const store = transaction.objectStore(storeName);
                    const task = await store.get(taskId);
                    const newStatus = task.status === 'uncompleted' ? 'completed' : 'uncompleted';
                    const updates = {
                        status: newStatus,
                        completionDate: newStatus === 'completed' ? Date.now() : null
                    };
                    await updateTask(taskId, updates);
                }
                renderTasks();
            }
        });

        function renderTasks() {
            const taskList = document.getElementById('task-list');
            taskList.innerHTML = '';
            getTasks().then(tasks => {
                tasks.forEach(task => {
                    const taskElement = document.createElement('div');
                    taskElement.className = 'task';
                    taskElement.innerHTML = `
                        <div class="task-content">${task.content}</div>
                        <div class="task-status">Status: ${task.status}</div>
                        <div>Registered: ${new Date(task.registrationDate).toLocaleDateString()}</div>
                        <div>Deadline: ${new Date(task.deadline).toLocaleDateString()}</div>
                        ${task.status === 'completed' ? `<div>Completed: ${new Date(task.completionDate).toLocaleDateString()}</div>` : ''}
                        <button data-task-id="${task.id}" data-action="delete">Delete</button>
                        <button data-task-id="${task.id}" data-action="update">Update Status</button>
                    `;
                    taskList.appendChild(taskElement);
                });
            });
        }

        renderTasks();
    </script>
</body>
</html>
