    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 20px;
}

h1 {
    margin-bottom: 20px;
}

#tasks-container {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
    gap: 10px;
    width: 100%;
    max-width: 1200px;
}

.task-card {
    background: rgba(255, 255, 255, 0.1);
    border-radius: 5px;
    padding: 15px;
    text-align: center;
    transition: transform 0.2s;
}

.task-card:hover {
    transform: translateY(-5px);
}

.task-completed {
    text-decoration: line-through;
    color: #ccc;
}

.task-info {
    margin-top: 10px;
}
